CREATE USER catalogue_user IDENTIFIED BY default_Password1;

GRANT CREATE SESSION TO catalogue_user;
GRANT UNLIMITED TABLESPACE TO catalogue_user;

CREATE ROLE catalogue_role;

GRANT catalogue_role TO catalogue_user;

CREATE TABLE catalogue_user.products (
	sku VARCHAR2(20) NOT NULL,
	brand VARCHAR2(20),
	title VARCHAR2(40),
	description VARCHAR2(500),
	weight VARCHAR2(10),
	product_size VARCHAR2(25),
	colors VARCHAR2(20),
	qty NUMBER(10, 0),
	price FLOAT, 
	image_url_1 VARCHAR2(50),
	image_url_2 VARCHAR2(50),
	PRIMARY KEY(sku)
);

CREATE TABLE catalogue_user.categories (
	category_id NUMBER(7,0) GENERATED BY DEFAULT ON NULL AS IDENTITY, 
	name VARCHAR2(30), 
	PRIMARY KEY(category_id)
);

CREATE TABLE catalogue_user.product_category (
	sku VARCHAR2(40), 
	category_id NUMBER(7,0) NOT NULL, 
	FOREIGN KEY (sku) 
		REFERENCES catalogue_user.products(sku), 
	FOREIGN KEY(category_id)
		REFERENCES catalogue_user.categories(category_id)
);

GRANT SELECT, INSERT, UPDATE, DELETE ON catalogue_user.products TO catalogue_role;
GRANT SELECT, INSERT, UPDATE, DELETE ON catalogue_user.categories TO catalogue_role;
GRANT SELECT, INSERT, UPDATE, DELETE ON catalogue_user.product_category TO catalogue_role;








quit;
